insert into TB_LIVRO(TITULO, DESCRICAO, ISBN, AUTOR, EDITORA, ANO_PUBLICACAO, FK_CATEGORIA) values ('O Senhor dos Anéis', 'O Senhor dos Anéis é uma trilogia de livros de fantasia escrita por J. R. R. Tolkien. A trilogia é composta por O Hobbit, O Senhor dos Anéis e O Retorno do Rei. A história se passa na Terra-média, um mundo fictício criado por Tolkien, e conta a jornada de um grupo de anões, hobbits, elfos e homens para destruir o Um Anel, um artefato mágico que foi criado pelo Sauron, um poderoso mago, e que foi roubado por seu antecessor, o Senhor das Trevas, o Rei dos Anões, o Rei dos Elfos e o Rei dos Homens.', '978-85-359-0277-5', 'J. R. R. Tolkien', 'Martins Fontes', 1954, 1);
insert into TB_LIVRO(TITULO, DESCRICAO, ISBN, AUTOR, EDITORA, ANO_PUBLICACAO, FK_CATEGORIA) values ('O Pequeno Príncipe', 'O Pequeno Príncipe é um livro de Antoine de Saint-Exupéry, publicado em 1943. O livro conta a história de um piloto que se encontra perdido no deserto do Saara e encontra um pequeno príncipe que vem de um planeta distante. O livro é considerado um clássico da literatura francesa e mundial.', '978-85-359-0277-5', 'Antoine de Saint-Exupéry', 'Martins Fontes', 1943, 1);
insert into TB_LIVRO(TITULO, DESCRICAO, ISBN, AUTOR, EDITORA, ANO_PUBLICACAO, FK_CATEGORIA) values ('O Código da Vinci', 'O Código da Vinci é um romance policial escrito pelo autor britânico Dan Brown. O livro foi publicado em 2003 e se tornou um best-seller internacional, vendendo mais de 80 milhões de cópias em todo o mundo. O livro é o primeiro de uma trilogia que inclui O Código da Vinci, Anjos e Demônios e O Símbolo Perdido.', '978-85-359-0277-5', 'Dan Brown', 'Martins Fontes', 2003, 2);
insert into TB_LIVRO(TITULO, DESCRICAO, ISBN, AUTOR, EDITORA, ANO_PUBLICACAO, FK_CATEGORIA) values ('O Diário de Anne Frank', 'O Diário de Anne Frank é um livro escrito por Anne Frank, uma garota judia que viveu na Holanda durante a Segunda Guerra Mundial. O livro é uma compilação de trechos do diário que Anne escreveu enquanto estava escondida com sua família e outros judeus na casa de um amigo de seus pais, Otto Frank, em Amsterdã.', '978-85-359-0277-5', 'Anne Frank', 'Martins Fontes', 1947, 2);
insert into TB_LIVRO(TITULO, DESCRICAO, ISBN, AUTOR, EDITORA, ANO_PUBLICACAO, FK_CATEGORIA) values ('A Culpa é das Estrelas', 'A Culpa é das Estrelas é um romance de John Green, publicado em 2012. O livro conta a história de Hazel Grace Lancaster, uma garota de 16 anos que tem câncer de pulmão e se apaixona por Augustus Waters, um garoto de 17 anos que perdeu uma perna em um acidente de bicicleta.', '978-85-359-0277-5', 'John Green', 'Martins Fontes', 2012, 4);
